# coding: utf-8import osimport hashlibimport jsonfrom apps.explorer.cmd import Commandsclass Bash(Commands):    ''' bash '''    def index(self):        tree = os.listdir(self.root_path)        return tree    def execute(self, post, action):        if action == 'tree':            if post['path']=='home':                for i in os.listdir(self.root_path):                    if os.path.isfile( os.path.join(self.root_path+post['path'],i) ):                        self.files.append(i)                    else:                        self.folders.append(i)            else:                for i in os.listdir(self.root_path+post['path']):                    if os.path.isfile( os.path.join(self.root_path+post['path'],i) ):                        self.files.append(i)                    else:                        self.folders.append(i)            answer = {                'path': post['path'],                'folders': self.folders,                'files': self.files                #'folders': [item for item in os.listdir(self.root_path+post['path']) ]            }                #self.result['tree'] = [item for item in os.listdir(self.root_path)]        return json.dumps(answer, ensure_ascii=False)